import { ColaboradorService } from './../../../services/colaborador.service';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';

@Component({
  selector: 'app-criar-colaborador',
  templateUrl: './criar-colaborador.component.html',
  styleUrls: ['./criar-colaborador.component.css']
})
export class CriarColaboradorComponent implements OnInit{

  formGroup!:FormGroup

  constructor(private router: Router,
    private service: ColaboradorService,
    private formBuilder: FormBuilder,
    private toast: ToastrService){
    }

  ngOnInit(): void {
    this.formGroup = this.formBuilder.group({
      id:[""],
      nome:[""],
      cpf:[""]
    })
  }

  salvar(){
    if(this.formGroup.valid){
      this.service.criar(this.formGroup.value).subscribe(()=>{
        this.router.navigate(['listar-colaboradores'])
      });
    } else{
      this.toast.error("Preencha os campos obrigatórios","Erro");
    }
  }


}
<h1>Cadastrar Colaborador</h1>
<form>
    <div class="mb-3">
      <label for="nome" class="form-label">Nome</label>
      <span class="container_required">(Campo Obrigatório)</span>
      <input type="text" class="form-control" id="nome">
    </div>
    <div class="mb-3">
      <label for="cpf" class="form-label">CPF</label>
      <span class="container_required">(Campo Obrigatório)</span>
      <input type="text" class="form-control" id="cpf" />
    </div>
    <button type="submit" class="btn btn-primary" (click)="salvar()">Cadastrar</button>
    <button type="submit" class="btn btn-primary" routerLink="/lista-colaboradores">Cancelar</button>
</form>
